using UnityEditor;
using UnityEngine;

namespace CookApps.Package.Report.Editor
{
    [FilePath("Packages/CookApps/PackagesReporter.asset", FilePathAttribute.Location.ProjectFolder)]
    internal class PackagesScriptable : ScriptableSingleton<PackagesScriptable>
    {
        //--------------------------------------------------------------------------------//
        //-----------------------------------FIELD----------------------------------------//
        //--------------------------------------------------------------------------------//
        //------------------- Inspector ------------------//


        //------------------- public ------------------//


        //------------------- protected ------------------//


        //------------------- private ------------------//
        [SerializeField]
        private bool _isCheckAllPackages = false;

        //--------------------------------------------------------------------------------//
        //------------------------------------PROPERTY------------------------------------//
        //--------------------------------------------------------------------------------//
        public bool IsCheckAllPackages => _isCheckAllPackages;

        //--------------------------------------------------------------------------------//
        //------------------------------------METHOD--------------------------------------//
        //--------------------------------------------------------------------------------//
        //───────────────────────────────────────────────────────────────────────────────────────
        internal void Check()
        {
            _isCheckAllPackages = true;
            Save(true);
        }
    }
}
